ALTER PROCEDURE dbo.spInsertarDIM

AS BEGIN
DECLARE @Emp	VARCHAR(10),
@EmpAnt	VARCHAR(10)
SELECT @EmpAnt = ''
WHILE(1=1)
BEGIN
SELECT @Emp = MIN(EMpresa)  FROM Empresa  WHERE Empresa > @EmpAnt
IF @Emp IS NULL BREAK
IF NOT EXISTS (SELECT 1 FROM DimCfg WHERE empresa=@Emp)
BEGIN
END
SELECT @EmpAnt = @Emp
END
END

