ALTER PROCEDURE spOmisionMensajeLayout
@Layout				varchar(50)

AS BEGIN
IF @Layout = 'BANAMEX'
BEGIN
DELETE MensajeLayout WHERE Layout = 'BANAMEX'
EXEC spOmisionMensajeLayoutBANAMEX
END
ELSE
IF @Layout = 'HSBC'
BEGIN
DELETE MensajeLayout WHERE Layout = 'HSBC'
END
ELSE
IF @Layout = 'HSBC2'
BEGIN
DELETE MensajeLayout WHERE Layout = 'HSBC2'
EXEC spOmisionMensajeLayoutHSBC2
END
ELSE
IF @Layout = 'SCOTIABANK'
BEGIN
DELETE MensajeLayout WHERE Layout = 'SCOTIABANK'
EXEC spOmisionMensajeLayoutSCOTIABANK
END
END

